    private final String keyVaultUrl;    public KeyVaultServiceImpl(String keyVaultUrl) {        this.keyVaultUrl = keyVaultUrl;    }    @Override    public Mono<String> getSecret(String key) {        SecretClient secretClient = new SecretClientBuilder()                .vaultUrl(keyVaultUrl)                .credential(new DefaultAzureCredentialBuilder().build())                .buildClient();        KeyVaultSecret retrievedSecret = secretClient.getSecret(key);        return Mono.just(retrievedSecret.getValue());    }