private void checkPermission() {    if (Build.VERSION.SDK_INT > Build.VERSION_CODES.M) {        if (getApplicationContext().checkSelfPermission(Manifest.permission.WRITE_EXTERNAL_STORAGE) == PackageManager.PERMISSION_DENIED) {            String[] permissions = {Manifest.permission.WRITE_EXTERNAL_STORAGE};            requestPermissions(permissions, 1);        } else {            importData();        }    } else {        importData();    }}