            // add provider            sharingIntent.type = &quot;*/*&quot;            sharingIntent.putExtra(Intent.EXTRA_STREAM, fileUri)            sharingIntent.flags = Intent.FLAG_GRANT_READ_URI_PERMISSION            activity.startActivity(sharingIntent)        } catch (ex: Exception) {            ex.printStackTrace()            Toast.makeText(activity, ex.message, Toast.LENGTH_LONG).show();        }    }}