@Servicepublic class EmployeeService {    @Autowired    EmployeeRepository employeeRepository;    @Autowired    ObjectMapper employeeObjectMapper;    public String getAllEmployeesService( ) throws JsonProcessingException {        List<Employee> employeeArrayList = employeeRepository.findAll();        GetAllEmployeesResponseWrapper getAllEmployeeResponseWrapper = new GetAllEmployeesResponseWrapper(employeeArrayList);        String response = employeeObjectMapper.writeValueAsString(getAllEmployeeResponseWrapper);        return response;    }}