@TestConfigurationpublic class TestRedisConfiguration {    private final RedisServer redisServer;    public TestRedisConfiguration(RedisProperties redisProperties) {        this.redisServer = new RedisServer(redisProperties.getRedisPort());    }    @Bean    EventsDataSourceProvider getEventsDataSourceProvider(){        return new EventsDataSourceProvider();    }    @PostConstruct    public void postConstruct() {        redisServer.start();    }    @PreDestroy    public void preDestroy() {        redisServer.stop();    }}