package br.com.uburu.spring.service;// Imports@Servicepublic class FilterService {    @Autowired    private FilterRepository repository;    public List<Filter> getAll() {        List<Filter> filters = new ArrayList<>();        repository.findAll().forEach(filters::add);        return filters;    }    public Filter findById(long id) {        return repository.findById(id).orElse(null);    }    public Filter save(Filter filter) {        return repository.save(filter);    }    public void delete(long id) {        repository.deleteById(id);    }    }