package ali.edu.topic;import androidx.appcompat.app.AppCompatActivity;import android.content.Intent;import android.os.Bundle;import android.view.View;// Result Activity//import ali.edu.topic.databinding.ActivityResoultBinding;public class ResoultActivity extends AppCompatActivity {    ActivityResoultBinding binding;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        binding = ActivityResoultBinding.inflate(getLayoutInflater());        setContentView(binding.getRoot());        int currectAns = getIntent().getIntExtra(&quot;currectAns&quot;,0);        int totalQuitions = getIntent().getIntExtra(&quot;total&quot;,0);        binding.scoor.setText(String.format(&quot;%d/%d&quot;, currectAns, totalQuitions));        binding.returnBtn.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(ResoultActivity.this, InnerActivity.class);                startActivity(intent);            }        });    }}// Inner Activitypackage ali.edu.topic;import androidx.annotation.Nullable;import androidx.appcompat.app.AppCompatActivity;import androidx.recyclerview.widget.LinearLayoutManager;import android.os.Bundle;import com.google.firebase.firestore.DocumentSnapshot;import com.google.firebase.firestore.EventListener;import com.google.firebase.firestore.FirebaseFirestore;import com.google.firebase.firestore.FirebaseFirestoreException;import com.google.firebase.firestore.QuerySnapshot;import java.util.ArrayList;import ali.edu.topic.databinding.ActivityInnerBinding;public class InnerActivity extends AppCompatActivity {    ActivityInnerBinding binding;    FirebaseFirestore database;    ArrayList<InnerModel> innerModels;    InAdapter Inadapter;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        binding = ActivityInnerBinding.inflate(getLayoutInflater());        setContentView(binding.getRoot());        database = FirebaseFirestore.getInstance();        String name = getIntent().getStringExtra(&quot;name&quot;);        binding.hadTitle.setText(name);        innerModels = new ArrayList<>();        Inadapter  = new InAdapter(this, innerModels);        database.collection(&quot;topics&quot;).document(name).collection(&quot;boards&quot;).orderBy(&quot;number&quot;)                .addSnapshotListener(new EventListener<QuerySnapshot>() {                    @Override                    public void onEvent(@Nullable QuerySnapshot value, @Nullable FirebaseFirestoreException error) {                        innerModels.clear();                        for (DocumentSnapshot snapshot : value.getDocuments()) {                            InnerModel model = snapshot.toObject(InnerModel.class);                            innerModels.add(model);                        }                        Inadapter.notifyDataSetChanged();                    }                });        binding.innerRecycleView.setLayoutManager(new LinearLayoutManager(this));        binding.innerRecycleView.setAdapter(Inadapter);    }}