@Beanpublic SecurityFilterChain filterChain(HttpSecurity http) throws Exception {    http            .cors()            .and()            .csrf()            .disable()            .exceptionHandling()            .authenticationEntryPoint(jwtAuthenticationEntryPoint)            .and()            .sessionManagement()            .sessionCreationPolicy(SessionCreationPolicy.STATELESS)            .and()            .authorizeHttpRequests()            .requestMatchers(AUTH_WHITE_LIST)            .permitAll()            .and()            .headers()            .frameOptions()            .disable()            .and()            .authorizeHttpRequests()            .anyRequest()            .authenticated()            .and()            .httpBasic()            .and()            .addFilterBefore(jwtAuthenticationFilter(), UsernamePasswordAuthenticationFilter.class)            .httpBasic();    return http.build();}