@BeanConnectionFactoryInitializer initializer(ConnectionFactory connectionFactory) {        ConnectionFactoryInitializer initializer = new ConnectionFactoryInitializer();        initializer.setConnectionFactory(connectionFactory);                ResourceDatabasePopulator databasePopulator = new ResourceDatabasePopulator();        databasePopulator.addScripts(new ClassPathResource(schema.sql));        initializer.setDatabasePopulator(databasePopulator);        return initializer;}