    @Override    protected MethodSecurityExpressionOperations createSecurityExpressionRoot(Authentication authentication,        MethodInvocation invocation) {            CustomMethodSecurityExpressionRoot root = new CustomMethodSecurityExpressionRoot(authentication, this.context);        root.setThis(invocation.getThis());        root.setPermissionEvaluator(getPermissionEvaluator());        root.setTrustResolver(getTrustResolver());        root.setRoleHierarchy(getRoleHierarchy());        root.setDefaultRolePrefix(getDefaultRolePrefix());        return root;    }