    // Ask the user to input a day of the week    Scanner input = new Scanner(System.in);    System.out.print(&quot;Please enter a day of the week: &quot;);    String day = input.nextLine();    // Check if the input day is valid    boolean validDay = false;    for (String d : daysOfWeek) {        if (d.equalsIgnoreCase(day)) {            validDay = true;            break;        }    }    if (!validDay) {        System.out.println(&quot;Invalid input. Please enter a valid day of the week.&quot;);    } else {        // Ask the user to select an option        System.out.println(&quot;Please select an option:&quot;);        System.out.println(&quot;1. Return the day&quot;);        System.out.println(&quot;2. Return the next day&quot;);        System.out.println(&quot;3. Return the previous day&quot;);        System.out.println(&quot;4. Add certain days to the day you set&quot;);        System.out.print(&quot;Option: &quot;);        int option = input.nextInt();        if (option == 1) {            System.out.println(&quot;The day is &quot; + day);        } else if (option == 2) {            int index = -1;            for (int i = 0; i < daysOfWeek.length; i++) {                if (daysOfWeek[i].equalsIgnoreCase(day)) {                    index = i;                    break;                }            }            String nextDay = daysOfWeek[(index + 1) % 7];            System.out.println(&quot;The next day is &quot; + nextDay);        } else if (option == 3) {            int index = -1;            for (int i = 0; i < daysOfWeek.length; i++) {                if (daysOfWeek[i].equalsIgnoreCase(day)) {                    index = i;                    break;                }            }            String prevDay = daysOfWeek[(index - 1 + 7) % 7];            System.out.println(&quot;The previous day is &quot; + prevDay);        } else if (option == 4) {            System.out.print(&quot;Enter the number of days to add: &quot;);            int numDays = input.nextInt();            int index = -1;            for (int i = 0; i < daysOfWeek.length; i++) {                if (daysOfWeek[i].equalsIgnoreCase(day)) {                    index = i;                    break;                }            }            String newDay = daysOfWeek[(index + numDays) % 7];            System.out.println(&quot;The new day is &quot; + newDay);        } else {            System.out.println(&quot;Invalid option. Please select a valid option.&quot;);        }    }}