private void setEndPoint(Object port, String endPoint) {          BindingProvider bp = (BindingProvider) port;    bp.getRequestContext().put(BindingProvider.ENDPOINT_ADDRESS_PROPERTY, endPoint);    Client client = ClientProxy.getClient(port);         Map<String, Object> props = new HashMap<>();    props.put(ConfigurationConstants.ACTION, ConfigurationConstants.USERNAME_TOKEN);    props.put(ConfigurationConstants.PASSWORD_TYPE, WSS4JConstants.PW_TEXT);    props.put(ConfigurationConstants.PW_CALLBACK_CLASS, InternalServicePasswordCallback.class.getName());    props.put(ConfigurationConstants.USER, user);    WSS4JOutInterceptor wssOut = new WSS4JOutInterceptor(props);    Endpoint endpoint = client.getEndpoint();    endpoint.getOutInterceptors().add(wssOut);         client.getOutInterceptors().add(new LoggingOutInterceptor(-1));    client.getInInterceptors().add(new LoggingInInterceptor(-1));}