public ByteArrayOutputStream downloadFileByChunks(String blobName) {    long file_size = 0;    long chunkSize = 10 * 1024 * 1024;    CloudBlockBlob blob = connectAndgetCloudBlockBlob(blobName);    ByteArrayOutputStream baos = new ByteArrayOutputStream();    try {        if (blob.exists()) {            blob.downloadAttributes();            file_size = blob.getProperties().getLength();            for (long i = 0; i < file_size; i += chunkSize) {                blob.downloadRange(i, file_size, baos);            }        }    } catch (StorageException e) {        throw new GenericException(e, BusinesErrorEnum.AZURE_BLOB_STORAGE_EXCEPTION);    }    return baos;}