public void downloadFileByChunks(String blobName, String filePath) {    long chunkSize = 10 * 1024 * 1024;    CloudBlockBlob blob = connectAndgetCloudBlockBlob(blobName);    try (FileOutputStream fos = new FileOutputStream(filePath)) {        if (blob.exists()) {            blob.downloadAttributes();            long fileSize = blob.getProperties().getLength();            for (long i = 0; i < fileSize; i += chunkSize) {                blob.downloadRange(i, Math.min(chunkSize, fileSize - i), fos);            }        }    } catch (StorageException | IOException e) {        throw new GenericException(e, BusinessErrorEnum.AZURE_BLOB_STORAGE_EXCEPTION);    }}